import styled from "styled-components";

const Input = styled.input`
    background-color: white;
    border: solid 1px #707070;
    font-size: 20px;
    border-radius: 20px;
`

export const TextInput = ({ name}) => {
    return(
        <div>
            <Input type="text" id={name} name={name}  placeholder={name.toUpperCase()}/>
        </div>
    )
}
